import React from "react";
import '../stylesheets/cards.css';
import {Hand} from '../interfaces';
import {suits} from '../enums';
import {getValueFromCardValueEnum} from '../helperFunctions';

type HandProps = {
    hand: Hand;
    playerName: string;
    playerBet?: number;
    show: boolean;
}

function HandComponent(props: HandProps) {

    return (
        <div className="Player">
            <h1>
                {props.playerName}
            </h1>
            <div className="Hand">
                <div className="Card">
                    {props.show ? <p className={props.hand.card1.suit === suits.Club ? "black" : props.hand.card1.suit === suits.Spade ? "green" : props.hand.card1.suit === suits.Heart ? "red" : "blue"}>
                        {getValueFromCardValueEnum(props.hand.card1.number)}{props.hand.card1.suit === suits.Club ? "♣️" : props.hand.card1.suit === suits.Spade ? "♠️" : props.hand.card1.suit === suits.Heart ? "♥" : "♦"}
                    </p> : null}
                </div>
                <div className="Card">
                    {props.show ? <p className={props.hand.card2.suit === suits.Club ? "black" : props.hand.card2.suit === suits.Spade ? "green" : props.hand.card2.suit === suits.Heart ? "red" : "blue"}>
                        {getValueFromCardValueEnum(props.hand.card2.number)}{props.hand.card2.suit === suits.Club ? "♣️" : props.hand.card2.suit === suits.Spade ? "♠️" : props.hand.card2.suit === suits.Heart ? "♥" : "♦"}
                    </p> : null }
                </div>
            </div>
            {props.playerBet ?
            <div className="PlayerBet">
                {props.playerBet!}
            </div>
            : null}

        </div>
    );
  }

  export default HandComponent;
