import React from "react";
import { useSubscription} from '@apollo/react-hooks';
import {subscriptionQuery} from '../queries';

type SubProps = {
    GAMEID: string
}

export default function SubHandler(Props: SubProps) {
    console.log("SubHandler gameid", Props.GAMEID);
    const { data, loading, error } = useSubscription(subscriptionQuery, { variables: { gameId: Props.GAMEID } });


    if(loading) {
      return (
        <div>loading</div>
      )
    }

    if(data) {
      console.log("sub onData ", data)
    }

    if(error) {
      return <div> ERROR</div>
    }

    return(
        <div>
            hello guvner! {data.toString()}
        </div>
    )
}
