import React, {useState} from "react";
import '../stylesheets/table.css';
import TableImage from '../assets/poker_table.png';
import HandComponent from './Hand';
import {Hand, Flop, SubscriptionPlayer} from '../interfaces';
import Action from './Action';
import TableCards from './TableCards';
import SitDown from './SitDown';
import SettingsOverlay from './SettingsOverlay';
import { useLocation } from "react-router-dom";
import { useSubscription} from '@apollo/react-hooks';
import {subscriptionQuery} from '../queries';
import {getCardFromSubscriptionCard} from '../helperFunctions';

  type TableProps = { 
  }

  export function Table(Props: TableProps) {
    const location: any = useLocation();
    const [isSitting, setIsSitting] = useState(false); 
    const [showSettingsOverlay, setShowSettingsOverlay] = useState(false);
    const [seatNumber, setSeatNumber] = useState(0);// eslint-disable-next-line
    const { data, loading, error } = useSubscription(subscriptionQuery, { variables: { gameId: location.state.gameId } });

    let potSize = 0;
    let dealer = 0;
    let numPlayers = 0;
    let flop: Flop = {};
    let player1: SubscriptionPlayer | undefined = undefined;
    let player2: SubscriptionPlayer | undefined = undefined;
    let player3: SubscriptionPlayer | undefined = undefined;
    let player4: SubscriptionPlayer | undefined = undefined;
    let player5: SubscriptionPlayer | undefined = undefined;
    let player6: SubscriptionPlayer | undefined = undefined;
    let player7: SubscriptionPlayer | undefined = undefined;
    let player8: SubscriptionPlayer | undefined = undefined;

    if (data) {
      console.log(data);
      potSize = data.change.potSize;
      dealer = data.change.dealer;
      numPlayers = data.change.numPlayers;
      flop = {
        card1: data.change.table.length > 0 ? getCardFromSubscriptionCard(data.change.table[0]) : undefined,
        card2: data.change.table.length > 1 ? getCardFromSubscriptionCard(data.change.table[1]) : undefined,
        card3: data.change.table.length > 2 ? getCardFromSubscriptionCard(data.change.table[2]) : undefined,
        card4: data.change.table.length > 3 ? getCardFromSubscriptionCard(data.change.table[3]) : undefined,
        card5: data.change.table.length > 4 ? getCardFromSubscriptionCard(data.change.table[4]) : undefined
      }
      player1 = data.change.players.find((player: SubscriptionPlayer) => {return player.position === 0})
      player2 = data.change.players.find((player: SubscriptionPlayer) => {return player.position === 1})
      player3 = data.change.players.find((player: SubscriptionPlayer) => {return player.position === 2})
      player4 = data.change.players.find((player: SubscriptionPlayer) => {return player.position === 3})
      player5 = data.change.players.find((player: SubscriptionPlayer) => {return player.position === 4})
      player6 = data.change.players.find((player: SubscriptionPlayer) => {return player.position === 5})
      player7 = data.change.players.find((player: SubscriptionPlayer) => {return player.position === 6})
      player8 = data.change.players.find((player: SubscriptionPlayer) => {return player.position === 7})
    }
    function dismissSettingsOverlay() {
      setShowSettingsOverlay(false);
    }

    function sitDown(seat: number) {
      console.log(seat);
      setSeatNumber(seat);
      setIsSitting(true);
      setShowSettingsOverlay(true);
    }

    function player(playerInfo: SubscriptionPlayer|undefined, seatNumber: number) {
      if(isSitting) {
        return playerInfo ? <HandComponent playerInfo={playerInfo}/> : <div className="emptyPlayer"/>
      } else {
        return playerInfo ? <HandComponent  playerInfo={playerInfo}/> : <SitDown seatNumber={seatNumber} sitDown={(seatNumber: number) => sitDown(seatNumber)}/>
      }
    }

    return(
      <div className="tableBackground">
        {showSettingsOverlay ? <SettingsOverlay showSettingsOverlay={() => dismissSettingsOverlay()} gameId={location.state.gameId} position={seatNumber}/> : null}
        <TableCards flop={flop} potSize={potSize}/>
        <div className="handLayer">
          <div className="row">
              { player(player1,0) }
              { player(player2,1) }
              { player(player3,2) }
          </div>

          <div className="row">
            { player(player4,3) }
            <img className="image" src={TableImage} alt={"Table"}/>
            { player(player5,4) }
          </div>

          <div className="row">
            { player(player6,5) }
            { player(player7,6) }
            { player(player8,7) }
          </div>
        <Action/>
        </div>
      </div>
    )
  }

  export default Table;
