import React, {useState} from "react";
import { useSubscription} from '@apollo/react-hooks';
import {subscriptionQuery} from '../queries';

type SubProps = {
    GAMEID: string
}

export default function SubHandler(Props: SubProps) {
    console.log("SubHandler gameid", Props.GAMEID);
    const [values, setValues] = useState<any>();

    const { data, loading, error } = useSubscription(subscriptionQuery, 
      { onSubscriptionData(options) {
          setValues(options);
        },
        variables: { 
          gameId: Props.GAMEID 
        }
      });


    if(error) {
      return <div> ERROR</div>
    }

    return(
        <div>
            hello guvner! {values}
        </div>
    )
}
