import React from "react";
import { useSubscription} from '@apollo/react-hooks';
import {subscriptionQuery} from '../queries';

type SubProps = {
    GAMEID: string
}

export default function SubHandler(Props: SubProps) {
    const { data, loading, error } = useSubscription(subscriptionQuery, { variables: { gameId: Props.GAMEID }, onSubscriptionData: (change) => console.log("sub notif!!!!!!!!", change) });
    console.log("subadubdub", Props.GAMEID);
    if(loading) {
      return (
        <div>loading</div>
      )
    }

    if(error) {
      return <div> ERROR</div>
    }

    return(
        <div>
            hello guvner! {data.change.gameState}
        </div>
    )
}
