import React, {useState} from "react";
import '../stylesheets/table.css';
import TableImage from '../assets/poker_table.png';
import HandComponent from './Hand';
import {Hand, Flop, Card} from '../interfaces';
import Action from './Action';
import TableCards from './TableCards';
import SitDown from './SitDown';
import SettingsOverlay from './SettingsOverlay';
import { useLocation } from "react-router-dom";
import { useSubscription} from '@apollo/react-hooks';
import {subscriptionQuery} from '../queries';
import {getCardFromSubscriptionCard} from '../helperFunctions';

  type TableProps = { 
  }

  export function Table(Props: TableProps) {
    const location: any = useLocation();
    const [isSitting, setIsSitting] = useState(false); 
    const [showSettingsOverlay, setShowSettingsOverlay] = useState(false);
    const [seatNumber, setSeatNumber] = useState(0);// eslint-disable-next-line
    const [p2hand, setP2Hand] = useState();// eslint-disable-next-line
    const [p3hand, setP3Hand] = useState();// eslint-disable-next-line
    const [p4hand, setP4Hand] = useState();// eslint-disable-next-line
    const [p5hand, setP5Hand] = useState();// eslint-disable-next-line
    const [p6hand, setP6Hand] = useState();// eslint-disable-next-line
    const [p7hand, setP7Hand] = useState();// eslint-disable-next-line
    const [p8hand, setP8Hand] = useState();// eslint-disable-next-line
    const { data, loading, error } = useSubscription(subscriptionQuery, { variables: { gameId: location.state.gameId } });

    let potSize = 0;
    let dealer = 0;
    let numPlayers = 0;
    let flop: Flop = {};
    let p1hand: Hand | null = null;

    if (data) {
      console.log(data);
      potSize = data.change.potSize;
      dealer = data.change.dealer;
      numPlayers = data.change.numPlayers;
      flop = {
        card1: data.change.table.length > 0 ? getCardFromSubscriptionCard(data.change.table[0]) : undefined,
        card2: data.change.table.length > 1 ? getCardFromSubscriptionCard(data.change.table[1]) : undefined,
        card3: data.change.table.length > 2 ? getCardFromSubscriptionCard(data.change.table[2]) : undefined,
        card4: data.change.table.length > 3 ? getCardFromSubscriptionCard(data.change.table[3]) : undefined,
        card5: data.change.table.length > 4 ? getCardFromSubscriptionCard(data.change.table[4]) : undefined
      }
    }

    console.log(flop);
    function dismissSettingsOverlay() {
      setShowSettingsOverlay(false);
    }

    function sitDown(seat: number) {
      console.log(seat);
      setSeatNumber(seat);
      setIsSitting(true);
      setShowSettingsOverlay(true);
    }

    function player(hand: Hand|undefined, seatNumber: number) {
      if(isSitting) {
        return hand ? <HandComponent hand={hand} playerName="player" show={true}/> : <div className="emptyPlayer"/>
      } else {
        return hand ? <HandComponent hand={hand} playerName="player" show={true}/> : <SitDown seatNumber={seatNumber} sitDown={(seatNumber: number) => sitDown(seatNumber)}/>
      }
    }

    return(
      <div className="tableBackground">
        {showSettingsOverlay ? <SettingsOverlay showSettingsOverlay={() => dismissSettingsOverlay()} gameId={location.state.gameId} position={seatNumber}/> : null}
        <TableCards flop={flop} potSize={potSize}/>
        <div className="handLayer">
          <div className="row">
              { player(p1hand!,1) }
              { player(p2hand,2) }
              { player(p3hand,3) }
          </div>

          <div className="row">
            { player(p4hand,4) }
            <img className="image" src={TableImage} alt={"Table"}/>
            { player(p5hand,5) }
          </div>

          <div className="row">
            { player(p6hand,6) }
            { player(p7hand,7) }
            { player(p8hand,8) }
          </div>
        <Action/>
        </div>
      </div>
    )
  }

  export default Table;
