import React from "react";
import '../stylesheets/cards.css';
import {SubscriptionPlayer} from '../interfaces';

type HandProps = {
    playerInfo: SubscriptionPlayer,
    hand: any,
    isDealer: boolean,
    actionPosition: number,
}

function YourHandComponent(props: HandProps) {
    console.log(props.hand)
    return (
        <div className="Player">
            <h1 className={props.isDealer ? "dealer" : "notDealer"}>
                {props.playerInfo.user.username}
            </h1>
            <div className={ props.actionPosition === props.playerInfo.position ? "ActionHand":"Hand"}>
                <div className={props.playerInfo.isFolded ? "Folded" : "Card"}>
                    <p className={props.hand.card1.suit === "c" ? "black" : props.hand.card1.suit === "s" ? "green" : props.hand.card1.suit === "h" ? "red" : "blue"}>
                        {props.hand.card1.number}{props.hand.card1.suit === "c" ? "♣️" : props.hand.card1.suit === "s" ? "♠️" : props.hand.card1.suit === "h" ? "♥" : "♦"}
                    </p> 
                </div>
                <div className={props.playerInfo.isFolded ? "Folded" : "Card"}>
                    <p className={props.hand.card2.suit === "c" ? "black" : props.hand.card2.suit === "s" ? "green" : props.hand.card2.suit === "h" ? "red"  : "blue"}>
                        {props.hand.card2.number}{props.hand.card2.suit === "c" ? "♣️" : props.hand.card2.suit === "s" ? "♠️" : props.hand.card2.suit === "h" ? "♥" : "♦"}
                    </p>  
                </div>
            </div>
            <div className="playerStack">
                {props.playerInfo.stack}
            </div>
            {props.playerInfo.betAmount > 0 ?
            <div className="PlayerBet">
                {props.playerInfo.betAmount}
            </div>
            : null}

        </div>
    );
  }

  export default YourHandComponent;
